	/**
	 * Get the SVG code for the specified icon
	 *
	 * @since 1.0.0
	 *
	 * @param string $icon Icon name.
	 * @param string $group Icon group.
	 * @param int $size The icon-size in pixels.
	 *
	 * @return string
	 */
	public static function get_svg( $icon, $class = '', $group = 'ui') {

		if ( 'ui' === $group ) {
			$arr = self::$ui_icons;
		} elseif ( 'social' === $group ) {
			$arr = self::$social_icons;
		} elseif ( 'shop' === $group ) {
			$arr = self::$shop_icons;
		} elseif ( 'mobile' === $group ) {
			$arr = self::$mobile_icons;
		} elseif ( 'widget' === $group ) {
			$arr = self::$widget_icons;
		} elseif ( 'metal' === $group ) {
			$arr = self::$metal_icons;
		} else {
			$arr = array();
		}

		$arr = apply_filters( "sepid_svg_icons_{$group}", $arr );

		$svg = '';

		if ( array_key_exists( $icon, $arr ) ) {
			$repl = '<svg aria-hidden="true" role="img" focusable="false" ';

			$svg = preg_replace( '/^<svg /', $repl, trim( $arr[ $icon ] ) );
		}

		return '<span class="spd-icon ' . esc_attr( $class ) . '">' . $svg . '</span>';
	}

